const getReedJobs = () => {
  const url = `https://cors-anywhere.herokuapp.com/http://www.reed.co.uk/api/1.0/search?keywords=${user.industry.replace(
    '"',
    ""
  )}&location=${user.city.replace('"', "")}&distancefromlocation=3`;
  return fetch(url, {
    headers: {
      "X-Requested-With": "lol",
      Authorization:
        "Basic NjE5OTY4ZDgtN2IyNy00NjFmLWExYTktMzJlMWI3MWZhZWM1Og=="
    }
  }).then(resp => resp.json());
};



class App extends React.Component {
  state = {
    count: 1,
    drivers: [],
  }

  componentDidMount() {
    fetchDrivers(this.state.count)
      .then(data => this.setState({
        drivers: data.drivers
      }))
    // this.setState({
    //   drivers: data.drivers
    // })
  }

  render() {
    const url = `https://maps.googleapis.com/maps/api/js?v=3.exp&libraries=geometry,drawing,places&key=${process.env.REACT_APP_GOOGLE_MAPS_API_KEY}`
    const lat = 51.5049375
    const lng = -0.0964509
    const drivers = this.state.drivers.slice(0, this.state.count)

    return (
      < div className="App" >
        <Map isMarkerShown={true}
          googleMapURL={url}
          lat={lat}
          lng={lng}
          drivers={drivers}
          loadingElement={<div style={{ height: `100%` }}> Loading... </div>}
          containerElement={<div style={{ height: `600px` }} />}
          mapElement={<div style={{ height: `100%` }} />} />
      </div>
    );
  }
}

export default App;
